<?php	//quotes('goog','select * from yahoo.finance.quotes where symbol=');	//quotes('goog','select * from yahoo.finance.quote where symbol=');	function quotes($symbol,$query){		$array =array();		$symbol = "\"".$symbol."\"  ";		$api = 'http://query.yahooapis.com/v1/public/yql?q=';		$query = $query.$symbol;		$params = '&env=http://datatables.org/alltables.env&format=json';		$session = curl_init($api.urlencode($query).$params);		curl_setopt($session, CURLOPT_RETURNTRANSFER,true);  		$json = curl_exec($session);		curl_close($session);		$yqlObj =  json_decode($json);		if(is_null($yqlObj){			return -1;		}		if(!is_null($yqlObj->query->results)){			return $yqlObj->query->results->quote;		}		return -1;	}	//keystats('goog','select * from yahoo.finance.keystats where symbol=');	function keystats($symbol,$query){		$array =array();		$symbol = "\"".$symbol."\"  ";		$api = 'http://query.yahooapis.com/v1/public/yql?q=';		$query = $query.$symbol;		$params = '&env=http://datatables.org/alltables.env&format=json';		$session = curl_init($api.urlencode($query).$params);		curl_setopt($session, CURLOPT_RETURNTRANSFER,true);  		$json = curl_exec($session);		curl_close($session);		$yqlObj =  json_decode($json);		if(is_null($yqlObj){			return -1;		}		if(!is_null($yqlObj->query->results)){			return $yqlObj->query->results->stats;		}		return -1;	}	//historicaldata('goog','select * from yahoo.finance.historicaldata where symbol=', ' and startDate="2009-09-11" and endDate="2010-03-10"');	function historicaldata($symbol,$query,$dates){		$array =array();		$symbol = "\"".$symbol."\"  ";		$api = 'http://query.yahooapis.com/v1/public/yql?q=';		$query = $query.$symbol.$dates;		$params = '&env=http://datatables.org/alltables.env&format=json';		$session = curl_init($api.urlencode($query).$params);		curl_setopt($session, CURLOPT_RETURNTRANSFER,true);  		$json = curl_exec($session);		curl_close($session);		$yqlObj =  json_decode($json);		if(is_null($yqlObj){			return -1;		}		if(!is_null($yqlObj->query->results)){			return $yqlObj->query->results->quote;		}		return -1;	}?>